mapper.select:
SELECT id, userid, starttime, endtime, duration
FROM tbl_session
WHERE userid = #{userid}
    AND MONTH(starttime) = #{month}
  AND YEAR(starttime) = #{year}
ORDER BY starttime ASC

SELECT id, userid, starttime, endtime, duration
FROM tbl_session
WHERE userid = #{userid}
  AND DATE(starttime) = #{date}
ORDER BY starttime ASC
